    <?php    /**    * 1.セッションスタート    */    session_start();    /**    * 2.ワンタイムチケットの生成    */    $ticket = md5(uniqid(mt_rand(),TRUE));    $_SESSION['ticket'] = $ticket;    //画像が不要になった場合はセッションで保持していた画像を削除    if(isset($_GET['action']) && $_GET['action']=='delete_image'){        unset($_SESSION['upfile']);    }    ?>    <!DOCTYPE HTML>    <html>    <head>        <meta charset="UTF-8">        <title>入力画面</title>            </head>    <body ng-controller="MainCtrl">        <div id="form">            <form name="form" method="post" action="./confirm.php" enctype="multipart/form-data">            <input type="hidden" id="referer" name="referer" value="input">            <input type="hidden" id="ticket" name="ticket" value="<?php echo $ticket; ?>">                <table cellpadding="0" cellspacing="0">                    <tr>                        <th colspan="2">入力画面</th>                    </tr>                    <tr>                        <td>お名前</td>                        <td>                            <?php if(isset($_SESSION['errMsg']['name']) && $_SESSION['errMsg']['name'] != ""){ echo "<span class='err'>".$_SESSION['errMsg']['name']."</span><br>"; } ?>                            <input type="text" id="name" name="name" value="<?php if(isset($_SESSION['name'])){ echo $_SESSION['name']; } ?>">                                                    </td>                    </tr>                    <tr>                        <td>メールアドレス</td>                        <td>                            <?php if(isset($_SESSION['errMsg']['mail']) && $_SESSION['errMsg']['mail'] != ""){ echo "<span class='err'>".$_SESSION['errMsg']['mail']."</span><br>"; } ?>                            <input type="email" id="mail" name="mail" value="<?php if(isset($_SESSION['mail'])){ echo $_SESSION['mail']; } ?>">                                                    </td>                    </tr>                    <tr>                        <td>内容</td>                        <td>                            <?php if(isset($_SESSION['errMsg']['text']) && $_SESSION['errMsg']['text'] != ""){ echo "<span class='err'>".$_SESSION['errMsg']['text']."</span><br>"; } ?>                            <textarea id="text" name="text" rows="5" cols="30"><?php if(isset($_SESSION['text'])){ echo $_SESSION['text']; } ?></textarea>                                                    </td>                    </tr>                    <tr>                        <td>添付画像</td>                        <td>                        <!-- <input type="hidden" name="MAX_FILE_SIZE" value="1580000" /> -->                        <?php if(isset($_SESSION['errMsg']['upfile']) && $_SESSION['errMsg']['upfile'] != ""){ echo "<span class='err'>".$_SESSION['errMsg']['upfile']."</span><br>"; } ?>                                            <input type="file" name="upfile">                        <?php if(isset($_SESSION['upfile'])){                            echo '<img src="files/'.$_SESSION['upfile'].'">';                            echo '<a href="?action=delete_image">削除</a>';                        } ?>                                         </td>                    </tr>                    <tr>                        <td class="center" colspan="2">                            <button type="submit" class="btn btn-primary">確認する</button>                        </td>                    </tr>                </table>            </form>        </div>    </body>    </html>