l->Encoding = "7bit";       $mail->AddAddress($mailto);        $mail->From = $mailfrom;                                $mail->FromName = mb_encode_mimeheader(mb_convert_encoding($fromname,"JIS","UTF-8"));   $mail->Subject = mb_encode_mimeheader(mb_convert_encoding($subject,"JIS","UTF-8"));     $mail->Body  = mb_convert_encoding($body,"JIS","UTF-8");                $mail->AddAttachment($attachfile);  //ADMIN MAIL  $mailto_admin   = $mailto02;      $subject_admin  = $subject02;      $body_admin    = $body_admin;        $attachfile   = "./files/".$_SESSION['upfile'];    $mail_admin = new PHPMailer();        $mail_admin->CharSet = "iso-2022-jp";     $mail_admin->Encoding = "7bit";       $mail_admin->AddAddress($mailto_admin);        $mail_admin->From = $mailfrom;                                $mail_admin->FromName = mb_encode_mimeheader(mb_convert_encoding($fromname,"JIS","UTF-8"));   $mail_admin->Subject = mb_encode_mimeheader(mb_convert_encoding($subject_admin,"JIS","UTF-8"));     $mail_admin->Body  = mb_convert_encoding($body_admin,"JIS","UTF-8");                $mail_admin->AddAttachment($attachfile);      //メールを送信_管理者    if (!$mail_admin->Send()){                                echo("Failed to send mail. Error:".$mail_admin->ErrorInfo);    }    //自動返信モードであれば問い合わせ者にもメールを送信    if($remail===1){        if (!$mail->Send()){                                    echo("Failed to send mail. Error:".$mail->ErrorInfo);        }    }    /**    * 5.セッション破棄及び添付画像の削除    */    unlink("./files/".$_SESSION['upfile']);    unset($_SESSION);    session_unset();    session_destroy();    ?>    <!DOCTYPE HTML>    <html>    <head>        <meta charset="UTF-8">        <title>完了画面</title>            </head>    <body>        <div id="form">            <table cellpadding="0" cellspacing="0">                <tr>                    <th>完了画面</th>                </tr>                <tr>                    <td class="center">メール送信が完了いた